pragma solidity ^0.7.0;

import "../interfaces/IBambooChef.sol";
import "../interfaces/IRouter.sol";
import "../interfaces/IPair.sol";
import "./BaseStrategy.sol";

contract BambooLPWithReinvestFee is BaseStrategy {
    using SafeMath for uint;

    uint public PID;
    uint public REINVEST_REWARD_BIPS = 500;
    uint public ADMIN_FEE_BIPS = 500;
    uint public MIN_TOKENS_TO_REINVEST = 20000;

    address public feeCollector;
    IBambooChef public stakingContract;
    IRouter public router;

    event UpdateAdminFee(uint oldValue, uint newValue);
    event UpdateReinvestReward(uint oldValue, uint newValue);
    event UpdateMinTokensToReinvest(uint oldValue, uint newValue);

    constructor(
        string memory _name,
        address _vault,
        address _depositToken,
        address _rewardToken,
        address _stakingContract,
        address _router,
        uint _pid
    ) {
        vault = _vault;
        depositToken = IERC20(_depositToken);
        rewardToken = IERC20(_rewardToken);

        stakingContract = IBambooChef(_stakingContract);
        router = IRouter(_router);
        PID = _pid;

        strategyManager = msg.sender;
        feeCollector = msg.sender;

        name = _name;
        setAllowances();
    }

    function setAllowances() public override onlyManager {
        depositToken.approve(vault, UINT_MAX);
        depositToken.approve(address(stakingContract), UINT_MAX);
        depositToken.approve(address(router), UINT_MAX);

        rewardToken.approve(address(router), UINT_MAX);

        IERC20 token0 = IERC20(IPair(address(depositToken)).token0());
        token0.approve(address(router), UINT_MAX);

        IERC20 token1 = IERC20(IPair(address(depositToken)).token1());
        token1.approve(address(router), UINT_MAX);
    }

    function deposit(uint amount) external override onlyVault {
        require(depositToken.transferFrom(msg.sender, address(this), amount), "Strategy::deposit, transferFrom failed");
        _stakeDepositTokens(amount);
    }

    function withdraw(address account, uint amount) external override onlyVault {
        _withdrawDepositTokens(amount);
        require(depositToken.transfer(account, amount), "Strategy::withdraw, transfer failed");
    }

    function reinvest() external override onlyVault returns (uint) {
        uint unclaimedRewards = checkReward();
        if (unclaimedRewards < MIN_TOKENS_TO_REINVEST) {
            return 0;
        }
        return _reinvest(unclaimedRewards);
    }

    function estimateReinvestReward() external override view returns (uint) {
        uint unclaimedRewards = checkReward();
        if (unclaimedRewards < MIN_TOKENS_TO_REINVEST) {
            return 0;
        }
        return unclaimedRewards.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    }

    /**
     * @notice Reward token balance that can be reinvested
     * @dev Staking rewards accurue to contract on each deposit/withdrawal
     * @return Unclaimed rewards, plus contract balance
     */
    function checkReward() public override view returns (uint) {
        uint pendingReward = stakingContract.pendingBamboo(PID, address(this));
        uint contractBalance = rewardToken.balanceOf(address(this));
        return pendingReward.add(contractBalance);
    }

    function getDeployedBalance() external override view returns (uint) {
        (uint amount,) = stakingContract.userInfo(PID, address(this));
        return amount;
    }

    /**
     * @notice Reinvest rewards from staking contract to deposit tokens
     * @dev This private function does not require mininmum tokens to be met
     * @dev Reward should go to tx.origin
     * @param amount reward tokens
     * @return deposit tokens reinvested
     */
    function _reinvest(uint amount) private returns (uint) {
        stakingContract.deposit(PID, 0);

        uint adminFee = amount.mul(ADMIN_FEE_BIPS).div(BIPS_DIVISOR);
        if (adminFee > 0) {
            require(rewardToken.transfer(feeCollector, adminFee), "Strategy::_reinvest, admin fee transfer failed");
        }

        uint reinvestFee = amount.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
        if (reinvestFee > 0) {
            require(rewardToken.transfer(tx.origin, reinvestFee), "Strategy::_reinvest, reinvest reward transfer failed");
        }

        uint depositTokenAmount = _convertRewardTokensToDepositTokens(amount.sub(adminFee).sub(reinvestFee));
        _stakeDepositTokens(depositTokenAmount);
        return depositTokenAmount;
    }

    /**
     * @notice Stakes deposit tokens in Staking Contract
     * @param amount deposit tokens to stake
     */
    function _stakeDepositTokens(uint amount) private {
        require(amount > 0, "Strategy::_stakeDepositTokens, amount too low");
        stakingContract.deposit(PID, amount);
    }

    /**
     * @notice Withdraws deposit tokens from Staking Contract
     * @dev Reward tokens are automatically collected
     * @dev Reward tokens are not automatically reinvested
     * @param amount deposit tokens to remove
     */
    function _withdrawDepositTokens(uint amount) private {
        require(amount > 0, "Strategy::_withdrawDepositTokens, amount too low");
        stakingContract.withdraw(PID, amount);
    }

    /**
     * @notice Converts entire reward token balance to deposit tokens
     * @dev Always converts through router; there are no price checks enabled
     * @return deposit tokens received
     */
    function _convertRewardTokensToDepositTokens(uint amount) private returns (uint) {
        uint amountIn = amount.div(2);
        require(amountIn > 0, "amount too low");

        // swap to token0
        address token0 = IPair(address(depositToken)).token0();
        address[] memory path0 = new address[](2);
        path0[0] = address(rewardToken);
        path0[1] = token0;

        uint amountOutToken0 = amountIn;
        if (path0[0] != path0[1]) {
            uint[] memory amountsOutToken0 = router.getAmountsOut(amountIn, path0);
            amountOutToken0 = amountsOutToken0[amountsOutToken0.length - 1];
            router.swapExactTokensForTokens(amountIn, amountOutToken0, path0, address(this), block.timestamp);
        }

        // swap to token1
        address token1 = IPair(address(depositToken)).token1();
        address[] memory path1 = new address[](2);
        path1[0] = path0[0];
        path1[1] = token1;

        uint amountOutToken1 = amountIn;
        if (path1[0] != path1[1]) {
            uint[] memory amountsOutToken1 = router.getAmountsOut(amountIn, path1);
            amountOutToken1 = amountsOutToken1[amountsOutToken1.length - 1];
            router.swapExactTokensForTokens(amountIn, amountOutToken1, path1, address(this), block.timestamp);
        }

