"libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":false,"runs":200},"remappings":[]},"sources":{"contracts/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "./YakERC20.sol";
import "./lib/SafeMath.sol";
import "./lib/Ownable.sol";
import "./strategies/BaseStrategy.sol";
import "./interfaces/IStakingRewards.sol";
import "./interfaces/IRouter.sol";
import "./interfaces/IPair.sol";
import "./interfaces/IERC20.sol";

contract Vault is YakERC20, Ownable {
    using SafeMath for uint;

    /// @notice Vault version number
    string public constant version = "0.0.1";

    /// @notice Total amount of `depositToken` managed by Vault
    uint public totalDeposits;

    /// @notice Deposit token
    IERC20 public depositToken;

    /// @notice Strategy contract
    address public strategyContract;

    /// @notice Admin role to update strategy (separate to `owner`)
    address public strategyUpdater;

    /// @notice Event emitted after successful `deposit()`
    event Deposit(address indexed account, uint amount);

    /// @notice Event emitted after successful `withdraw()`
    event Withdraw(address indexed account, uint amount);

    /// @notice Event emitted after reinvest. Used to track growth
    event Reinvest(uint newTotalDeposits, uint newTotalSupply);

    /// @notice Event emitted when Vault tokens are recovered
    event Recovered(address indexed token, uint amount);

    /// @notice Event emitted when `strategyContract` is updated
    event UpdateStrategy(address indexed oldValue, address indexed newValue);

    /// @notice Event emitted when `strategyUpdater` is updated
    event TransferStrategyUpdaterRole(address indexed oldValue, address indexed newValue);

    constructor (
        string memory _name,
        address _depositToken
    ) {
        name = _name;
        depositToken = IERC20(_depositToken);

        strategyUpdater = msg.sender;
    }

    /**
     * @dev Throws if called by smart contract
     */
    modifier onlyEOA() {
        require(tx.origin == msg.sender, "Vault::onlyEOA, invalid msg.sender");
        _;
    }

    /**
     * @dev May only be called by `strategyUpdater`
     */
    modifier onlyStrategyUpdater() {
        require(msg.sender != address(0), "Vault::onlyStrategyUpdater, invalid msg.sender");
        require(msg.sender == strategyUpdater, "Vault::onlyStrategyUpdater, not strategy updater");
        _;
    }

    /**
     * @notice Revoke token allowance
     * @dev Restricted to owner to avoid griefing attacks
     * @param token address
     * @param spender address
     */
    function revokeAllowance(address token, address spender) external onlyOwner {
        require(IERC20(token).approve(spender, 0));
    }

    /**
     * @notice Deposit tokens to receive receipt tokens
     * @param amount Amount of tokens to deposit
     */
    function deposit(uint amount) external {
        _deposit(msg.sender, amount);
    }

    /**
     * @notice Deposit on behalf of another address
     * @param account address to receipt receipt tokens
     * @param amount Amount of tokens to deposit
     */
    function depositOnBehalfOf(address account, uint amount) external {
        _deposit(account, amount);
    }

    /**
     * @notice Deposit using Permit
     * @param amount Amount of tokens to deposit
     * @param deadline The time at which to expire the signature
     * @param v The recovery byte of the signature
     * @param r Half of the ECDSA signature pair
     * @param s Half of the ECDSA signature pair
     */
    function depositWithPermit(uint amount, uint deadline, uint8 v, bytes32 r, bytes32 s) external {
        depositToken.permit(msg.sender, address(this), amount, deadline, v, r, s);
        _deposit(msg.sender, amount);
    }

    /**
     * @notice Private function to handle user deposits
     * @dev If there is no strategy, deposits are held in Vault
     * @param account address to receive receipt tokens
     * @param amount deposit tokens to deposit
     */
    function _deposit(address account, uint amount) private {
        require(amount > 0, "Vault::_deposit, amount too low");
        require(depositToken.transferFrom(msg.sender, address(this), amount), "Vault::_deposit, transferFrom failed");
        if (strategyContract != address(0)) {
            BaseStrategy(strategyContract).deposit(amount);
        }
        _mint(account, getSharesForDepositTokens(amount));
        totalDeposits = totalDeposits.add(amount);
        emit Deposit(account, amount);
    }

    /**
     * @notice Withdraw deposit tokens by redeeming receipt tokens
     * @dev If strategy is set, tries to withdraw from strategy
     * @dev If strategy is NOT set, tries to withdraw from vault
     * @param amount receipt tokens to redeem
     */
    function withdraw(uint amount) external {
        require(amount > 0, "Vault::withdraw, amount too low");
        uint depositTokenAmount = getDepositTokensForShares(amount);
        require(depositTokenAmount > 0, "Vault::withdraw, deposit amount too low");
        if (strategyContract != address(0)) {
            BaseStrategy(strategyContract).withdraw(msg.sender, depositTokenAmount);
        }
        else if (depositToken.balanceOf(address(this)) >= depositTokenAmount) {
            require(depositToken.transfer(msg.sender, depositTokenAmount), "Vault::withdraw, transfer failed");
        }
        else {
            revert("Vault::withdraw, failed");
        }
        _burn(msg.sender, amount);
        totalDeposits = totalDeposits.sub(depositTokenAmount);
        emit Withdraw(msg.sender, depositTokenAmount);
    }

    /**
     * @notice Reinvest rewards from staking contract to deposit tokens
     */
    function reinvest() external onlyEOA {
        require(strategyContract != address(0), "Vault::reinvest, no strategy");
        uint depositTokensAdded = BaseStrategy(strategyContract).reinvest();
        require(depositTokensAdded > 0, "Vault::reinvest, not enough deposit tokens");
        totalDeposits = totalDeposits.add(depositTokensAdded);
        emit Reinvest(totalDeposits, totalSupply);
    }

    /**
     * @notice Estimate reinvest reward for caller
     * @return Estimated rewards tokens earned for calling `reinvest()`
     */
    function estimateReinvestReward() external view returns (uint) {
        if (strategyContract == address(0)) {
            return 0;
        }
        return BaseStrategy(strategyContract).estimateReinvestReward();
    }

    /**
     * @notice Reward token balance that can be reinvested
     * @return Reward token balance that can be reinvested
     */
    function checkReward() external view returns (uint) {
        if (strategyContract == address(0)) {
            return 0;
        }
        return BaseStrategy(strategyContract).checkReward();
    }

    /**
     * @notice Calculate receipt tokens for a given amount of deposit tokens
     * @dev If contract is empty, use 1:1 ratio
     * @dev Could return zero shares for very low amounts of deposit tokens
     * @param amount deposit tokens
     * @return receipt tokens
     */
    function getSharesForDepositTokens(uint amount) public view returns (uint) {
        if (totalSupply.mul(totalDeposits) == 0) {
            return amount;
        }
        return amount.mul(totalSupply).div(totalDeposits);
    }

    /**
     * @notice Calculate deposit tokens for a given amount of receipt tokens
     * @param amount receipt tokens
     * @return deposit tokens
     */
    function getDepositTokensForShares(uint amount) public view returns (uint) {
        if (totalSupply.mul(totalDeposits) == 0) {
            return 0;
        }
        return amount.mul(totalDeposits).div(totalSupply);
    }

    /**
     * @notice Revoke deployed `depositTokens` back into Vault
     * @param strategy address
     */
    function revokeDeployedFunds(address strategy) external onlyOwner {
        BaseStrategy(strategy).rescueDeployedFunds();
        BaseStrategy(strategy).recoverERC20(
            address(depositToken), depositToken.balanceOf(strategy)
        );
    }

    /**
     * @notice Sweeps tokens from Strategy to Vault
     * @param strategy address
     * @param tokens list of token addresses
     */
    function sweepTokensFrom(address strategy, address[] calldata tokens) external onlyOwner {
        for(uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == address(0)) {
                BaseStrategy(strategy).recoverAVAX(
                    address(this).balance
                );
            }
            else {
                IERC20 token = IERC20(tokens[i]);
                BaseStrategy(strategy).recoverERC20(
                    tokens[i], 
                    token.balanceOf(strategy)
                );
            }
        }
    }

    /**
     * @notice Deploy funds from Vault into a Strategy
     * @dev Intended for use as part of an upgrade process
     * @dev Does not affect `totalDeposits` nor mint receipt tokens
     */
    function deployStrategyFunds(uint amount) external onlyStrategyUpdater {
        require(strategyContract != address(0), "Vault::deployStrategyFunds, no strategy");
        BaseStrategy(strategyContract).deposit(amount);
    }

    /**
     * @notice Revokes current strategy
     */
    function revokeStrategy() external onlyStrategyUpdater {
        require(strategyContract != address(0), "Vault::revokeStrategy, no strategy");
        require(depositToken.approve(strategyContract, 0));
        emit UpdateStrategy(strategyContract, address(0));
        strategyContract = address(0);
    }

    /**
     * @notice Updates strategy contract
     * @dev Checks for matching `vault` and `depositToken` values
     * @param newStrategy contract address of new strategy
     */
    function updateStrategy(address newStrategy) external onlyStrategyUpdater {
        require(newStrategy != address(0), "Vault::updateStrategy, use revokeStrategy() instead");
        require(
            BaseStrategy(newStrategy).vault() == address(this)
            && BaseStrategy(newStrategy).depositToken() == depositToken,
            "Vault::updateStrategy, incompatible strategy");
        require(depositToken.approve(newStrategy, uint256(-1)));
        emit UpdateStrategy(strategyContract, newStrategy);
        strategyContract = newStrategy;
    }

    /**
     * @notice Transfer admin role `strategyUpdater`
     * @dev Set value to address(0) to disable the role and prevent future transfers
     * @param newStrategyUpdater address of new strategy updater
     */
    function transferStrategyUpdaterRole(address newStrategyUpdater) external onlyStrategyUpdater {
        emit TransferStrategyUpdaterRole(strategyUpdater, newStrategyUpdater);
        strategyUpdater = newStrategyUpdater;
    }

    /**
     * @notice Recover ERC20 from Vault
     * @dev Cannot be used to recover deposit tokens
     * @param tokenAddress token address
     * @param tokenAmount amount to recover
     */
    function recoverERC20(address tokenAddress, uint tokenAmount) external onlyOwner {
        require(tokenAddress != address(depositToken), "Vault::recoverERC20, cannot recover deposit token");
        require(tokenAmount > 0, "Vault::recoverERC20, amount too low");
        require(IERC20(tokenAddress).transfer(msg.sender, tokenAmount), "Vault::recoverERC20, transfer failed");
        emit Recovered(tokenAddress, tokenAmount);
    }

    /**
     * @notice Recover AVAX from Vault
     * @param amount amount to recover
     */
    function recoverAVAX(uint amount) external onlyOwner {
        require(amount > 0, "Vault::recoverAVAX, amount too low");
