abstract contract BaseStrategy {
    using SafeMath for uint;

    IERC20 public depositToken;
    IERC20 public rewardToken;
    address public vault;
    address public strategyManager;
    string public name;

    uint constant internal BIPS_DIVISOR = 10000;
    uint constant internal UINT_MAX = uint256(-1);

    /**
     * @dev May only be called by `vault`
     */
    modifier onlyVault() {
        require(msg.sender == vault, "onlyVault");
        _;
    }

    /**
     * @dev May only be called by `strategyManager`
     */
    modifier onlyManager() {
        require(msg.sender == strategyManager, "onlyManager");
        _;
    }

    /**
     * @dev May only be called by `strategyManager` or `vault`
     */
    modifier onlyManagerOrVault() {
        require(
            msg.sender == strategyManager
            || msg.sender == vault
            , "onlyManagerOrVault"
        );
        _;
    }

    /**
     * @notice Approve tokens for use in Strategy
     * @dev Restricted to avoid griefing attacks
     */
    function setAllowances() public virtual;

    /**
     * @notice Revoke token allowance
     * @dev Restricted to avoid griefing attacks
     * @param token address
     * @param spender address
     */
    function revokeAllowance(address token, address spender) external onlyManager {
        require(IERC20(token).approve(spender, 0));
    }

    /**
     * @notice Deposits `depositToken` to strategy
     * @param amount deposit tokens to deposit to strategy
     */
    function deposit(uint amount) external virtual;

    /**
     * @notice Withdraws `depositTokens` from strategy
     * @param account account to receive deposit tokens
     * @param amount deposit tokens to withdraw from strategy
     */
    function withdraw(address account, uint amount) external virtual;

    /**
     * @notice Reinvests `rewardToken` to `depositToken`
     * @return deposit tokens received
     */
    function reinvest() external virtual returns (uint);

    /**
     * @notice Estimate reinvest reward for caller
     * @return Estimated rewards tokens earned for calling `reinvest()`
     */
    function estimateReinvestReward() external virtual view returns (uint);

    /**
     * @notice Reward token balance that can be reinvested
     * @return reward tokens
     */
    function checkReward() public virtual view returns (uint);

